Events.GetParameters:
(block (
	(damageHP (@ gData 'damageHP))
	(speed (@ gData 'speed))
	)
	{
		length:	(mathScale damageHP	2 30 speed (@ speed 2) 50)
		width: (mathScale damageHP 2 30 12 28 50)
		}
	)

(block (damageHP)
	(setq damageHP (@ gData 'damageHP))

	{
		length:	(mathScale damageHP	2 30 (divide (@ gData 'speed) 2) (@ gData 'speed) 50)
		width: (mathScale damageHP 2 30 12 36 50)
		}
	)

(block (damageHP primaryColor secondaryColor)
	(setq damageHP (@ gData 'damageHP))

	{
		length:	(mathScale damageHP	5 64 16 64 50)
		width: (mathScale damageHP 5 64 12 48 50)
		intensity: 10
		}
	)

(block (damageHP primaryColor secondaryColor)
	(setq damageHP (@ gData 'damageHP))

	{
		length:	(mathScale damageHP 5 64 30 110 50)
		width: (mathScale damageHP 5 64 12 44 50)
		intensity: 30
		}
	)

(block (
	(damageHP (@ gData 'damageHP))
	)

	{
		radius: (mathScale damageHP 10 140 12 28 50)
		}
	)

(block (
	(damageHP (@ gData 'damageHP))
	)

	{
		length: (mathScale damageHP 10 140 24 56 50)
		width: (mathScale damageHP 10 140 18 42 50)
		}
	)

(block (damageHP primaryColor secondaryColor)
	(setq damageHP (@ gData 'damageHP))

	{
		length: (mathScale damageHP 10 140 60 260 50)
		width: (mathScale damageHP 10 140 20 40 50)
		intensity: 60
		}
	)

(block (
	(damageHP (@ gData 'damageHP))
	)

	{ 
		particleCount: (mathScale damageHP 10 140 100 300 50)
		length: (mathScale damageHP 10 140 60 220 50)
		width: (mathScale damageHP 10 140 6 16 50)
		}
	)

(block (
	(damageHP (@ gData 'damageHP))
	)

	{
		radius: (if (= damageHP 0) 10 (mathScale damageHP 10 140 30 70 50))
		lifetime: (if (= damageHP 0) 4 (mathScale damageHP 10 140 12 48 50))
		}
	)

(block (
	(damageHP (@ gData 'damageHP))
	)

	{
		lifetime: (if (= damageHP 0) 10 (mathScale damageHP 10 140 12 48 50))
		emitRate: (mathScale damageHP 10 140 50 200 50)
		emitLifetime: (if (= damageHP 0) 2 (mathScale damageHP 10 140 6 24 50))
		}
	)

(block (
	(damageHP (@ gData 'damageHP))
	)

	{
		noSound: (= damageHP 0)
		}
	)

(block (
	(damageHP (@ gData 'damageHP))
	)

	{
		radius: (mathScale damageHP 10 140 12 28 50)
		}
	)

(block (
	(damageHP (@ gData 'damageHP))
	)

	{
		length: (mathScale damageHP 10 140 24 56 50)
		width: (mathScale damageHP 10 140 18 42 50)
		}
	)

(block (damageHP primaryColor secondaryColor)
	(setq damageHP (@ gData 'damageHP))

	{
		length: (mathScale damageHP 10 140 60 260 50)
		width: (mathScale damageHP 10 140 20 40 50)
		intensity: 60
		}
	)

(block (
	(damageHP (@ gData 'damageHP))
	(weaponUNID (@ gData 'weaponUNID))

	(damageDeviceDisrupt (itmGetProperty weaponUNID 'damageDeviceDisrupt))
	(intensity (+ 20 (* damageDeviceDisrupt 10)))
	)
	{
		length: (mathScale damageHP 10 140 60 260 50)
		width: (mathScale damageHP 10 140 10 20 50)
		intensity: intensity
		}
	)

(block (
	(damageHP (@ gData 'damageHP))
	)

	{
		radius: (if (= damageHP 0) 10 (mathScale damageHP 10 140 30 70 50))
		lifetime: (if (= damageHP 0) 4 (mathScale damageHP 10 140 12 48 50))
		}
	)

